import React, { Component } from "react";
import { createMuiTheme, useTheme } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";


const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: "center",
        color: theme.palette.text.primary,
        //margin: theme.spacing(1),
        //marginBottom: theme.spacing(1)
    },
}));

const theme = createMuiTheme();

function CenteredGrid() {
    const classes = useStyles(useTheme());
    const theme = useTheme();

    return (
        <Container fluid className={classes.container}>
            <Row className={classes.row} style={{marginBottom: theme.spacing(1)}}>
                <Col className={classes.col} style={{marginRight: theme.spacing(1)}}>
                    <Paper elevation={3} className={classes.paper}>
                        <h6>News Feed</h6>
                    </Paper>
                </Col>
                <Col className={classes.col} style={{marginLeft: theme.spacing(1)}}>
                    <Paper elevation={3} className={classes.paper} style={{marginBottom: theme.spacing(2)}}>
                        <h6>New Recommendations</h6>
                    </Paper>
                    <Paper elevation={3} className={classes.paper} style={{marginTop: theme.spacing(1)}}>
                        <h6>Favourite local stores</h6>
                    </Paper>
                </Col>
            </Row>
            <Row className={classes.row} style={{marginTop: theme.spacing(2)}}>
                <Col className={classes.col} style={{marginRight: theme.spacing(1)}}>
                    <Paper elevation={3} className={classes.paper}>
                        <h6 color={theme.palette.primary.main}>Events</h6>
                    </Paper>
                </Col>
                <Col className={classes.col} style={{marginLeft: theme.spacing(1)}}>
                    <Paper elevation={3} className={classes.paper}>
                        <h6>Favourite authors</h6>
                    </Paper>
                </Col>
            </Row>
        </Container>
    );
}

export class Dashboard extends Component {
    render() {
        return (
            <div style={dashboardStyle}>
                <CenteredGrid></CenteredGrid>
            </div>
        );
    }
}

const dashboardStyle = {
    marginTop: theme.spacing(2),
    paddingLeft: 0,
    paddingRight: 0
};

export default Dashboard;
