import React, { Component } from "react";
import { createMuiTheme } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import {Container, Row, Col} from "react-bootstrap"

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: "center",
        color: theme.palette.text.secondary,
        //marginTop: theme.spacing(1),
        //marginBottom: theme.spacing(1)
    }
}));

const theme = createMuiTheme();

function CenteredGrid() {
    const classes = useStyles();

    return (
        <Container fluid>
            <Row>
                <Col>
                    <Paper  elevation={2} className={classes.paper}>News Feed</Paper>
                </Col>
                <Col>
                    <Paper elevation={2} className={classes.paper}>New Recommendations</Paper>
                    <Paper elevation={2} className={classes.paper}>Favourite local stores</Paper>
                </Col>
            </Row>
            <Row>
                <Col>
                    <Paper elevation={2} className={classes.paper}>Events</Paper>
                </Col>
                <Col>
                    <Paper elevation={2} className={classes.paper}>Favourite authors</Paper>
                </Col>
            </Row>

        </Container>
    );
}

export class Dashboard extends Component {
    render() {
        return (
            <div className="container" style={dashboardStyle}>
                <CenteredGrid></CenteredGrid>
            </div>
        );
    }
}

const dashboardStyle = {
    marginTop: theme.spacing(2),
    marginLeft: theme.spacing(16),
    marginRight: theme.spacing(16)
};

export default Dashboard;
