import React, { Component } from "react";
import { createMuiTheme } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,

    },
    paper: {
        padding: theme.spacing(2),
        textAlign: "center",
        color: theme.palette.text.secondary,
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1)
    },

    gridItem: {
        padding: 0,
        margin: theme.spacing(1)
    }
}));

const theme = createMuiTheme();

function CenteredGrid() {
    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Grid classname={classes.gridItem} container direction="row" justify="center" alignItems="stretch" spacing={2}>
                <Grid classname={classes.gridItem} item xs={6} alignItems="stretch">
                    <Paper  elevation={2} className={classes.paper}>News Feed</Paper>
                </Grid>
                <Grid container item direction="column"  alignItems="stretch" xs={6}>
                        <Paper elevation={2} className={classes.paper}>New Recommendations</Paper>
                        <Paper elevation={2} className={classes.paper}>Favourite local stores</Paper>
                </Grid>
            </Grid>
            <Grid container item direction="row" justify="center" alignItems="stretch" spacing={2}>
            <Grid item xs={6}>
                    <Paper elevation={2} className={classes.paper}>Events</Paper>
                </Grid>
                <Grid item xs={6}>
                    <Paper elevation={2} className={classes.paper}>Favourite authors</Paper>
                </Grid>
            </Grid>
        </div>
    );
}

export class Dashboard extends Component {
    render() {
        return (
            <div className="container" style={dashboardStyle}>
                <CenteredGrid></CenteredGrid>
            </div>
        );
    }
}

const dashboardStyle = {
    marginTop: theme.spacing(2),
    marginLeft: theme.spacing(16),
    marginRight: theme.spacing(16)
};

export default Dashboard;
