import React, { Component } from "react";
import { createMuiTheme } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import { Container, Row, Col } from "react-bootstrap";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: "center",
        color: theme.palette.text.secondary,
        //margin: theme.spacing(1),
        //marginBottom: theme.spacing(1)
    },
    col: {
        paddingLeft: 0,
        paddingRight: 0,
    },
    row: {
        paddingLeft: 0,
        paddingRight: 0,
        marginLeft: 0,
        marginRight: 0,
    },
    container: {
        paddingLeft: 0,
        paddingRight: 0,
        marginLeft: 0,
        marginRight: 0,
    }
}));

const theme = createMuiTheme();

function CenteredGrid() {
    const classes = useStyles();

    return (
        <Container fluid className={classes.container}>
            <Row className={classes.row} style={{marginBottom: theme.spacing(1)}}>
                <Col className={classes.col} style={{marginRight: theme.spacing(1)}}>
                    <Paper elevation={3} className={classes.paper}>
                        News Feed
                    </Paper>
                </Col>
                <Col className={classes.col} style={{marginLeft: theme.spacing(1)}}>
                    <Paper elevation={3} className={classes.paper} style={{marginBottom: theme.spacing(1)}}>
                        New Recommendations
                    </Paper>
                    <Paper elevation={3} className={classes.paper} style={{marginTop: theme.spacing(1)}}>
                        Favourite local stores
                    </Paper>
                </Col>
            </Row>
            <Row className={classes.row}>
                <Col className={classes.col} style={{marginRight: theme.spacing(1)}}>
                    <Paper elevation={3} className={classes.paper}>
                        Events
                    </Paper>
                </Col>
                <Col className={classes.col} style={{marginLeft: theme.spacing(1)}}>
                    <Paper elevation={3} className={classes.paper}>
                        Favourite authors
                    </Paper>
                </Col>
            </Row>
        </Container>
    );
}

export class Dashboard extends Component {
    render() {
        return (
            <div style={dashboardStyle}>
                <CenteredGrid></CenteredGrid>
            </div>
        );
    }
}

const dashboardStyle = {
    marginTop: theme.spacing(2),
    paddingLeft: 0,
    paddingRight: 0
};

export default Dashboard;
